CREATE PROCEDURE public.revert_users_when_prices_expired(IN bundleid integer, IN userid integer, IN actorid integer)
    LANGUAGE plpgsql
    AS $$
    BEGIN
        if exists (WITH prices as (
          select id from policy_bundle_prices where bundle_id = bundleid
        ) select * from user_switches where user_id=userid and selected_bundle_price_id in (select * from prices))
      then RETURN;
      ELSE
      WITH event as (
              UPDATE users
              SET
                stage = 4,
                updated_at = now()
              WHERE
                id = userid and stage = 10
              RETURNING
                actorid as actor_id,
                userid as user_id,
                'revert user stage to quote sent when policy bundle price expired' as action,
                '{"stage": 4}'::jsonb as new_values,
                '{"stage": 10}'::jsonb as old_values,
                format('{"bundle_id": %s}', bundleid)::jsonb as metadata,
                now() as event_timestamp,
                now() as created_at,
                userid as object_id,
                'User' as object_type
            )
            INSERT INTO user_events (actor_id, user_id, action, new_values, old_values, metadata, event_timestamp, created_at, object_id, object_type)
            SELECT * FROM event;
            update policy_bundle_prices set deleted_at=now(), updated_at=now() where bundle_id = bundleid;
      END IF;
    END;
    $$;


